var pathname = window.location.pathname.replace(/\//g, '');
if(pathname == 'cart') {
	frappe.provide("erpnext.shopping_cart");
	var shopping_cart = erpnext.shopping_cart;
}

$(document).ready(function(){

	$(".item-stock").hide();
	$(".item-cart").addClass("dont-hide");
  if(pathname == 'cart'){
    $(".page-header-actions-block").append("<button class='btn btn-primary btn-complete-order btn-sm'>Complete Order</button>");
    $("button.btn-place-order").hide();
    $(".btn-complete-order").click(function(){

      place_order();

    });
  }
	if(pathname == 'products' || pathname == ''){
		$( "i.fa-close" ).each(function( index ) {
			$(this).parent().hide()
		});
	}
	if($(".page-container").attr("data-doctype") == "Item") {
		get_user_price();
	}

});

function place_order() {
	return frappe.call({
		type: "POST",
		method: "origintheme.api.place_order",
		callback: function(r) {
			if(r.exc) {
				var msg = "";
				if(r._server_messages) {
					msg = JSON.parse(r._server_messages || []).join("<br>");
				}

				$("#cart-error")
					.empty()
					.html(msg || frappe._("Something went wrong!"))
					.toggle(true);
			} else {
				window.location.href = "/quotations/" + encodeURIComponent(r.message);
			}
		}
	});
}

function get_user_price() {
	var item_code = $('[itemprop="productID"]').html();
	frappe.call({
		type: "POST",
		method: "origintheme.api.get_item_price",
		args: {
			item_code: item_code
		},
		callback: function(r) {
			if(r.message && r.message.price_list_rate) {
				var price = r.message;
				price.price_list_rate = price.price_list_rate.toFixed(2).toString();
				$("h4.item-price").addClass("hide");
				$("h4.item-price").parent().append("<h4>$ "+price.price_list_rate+" per "+price.stock_uom+"</h4>");
			}

		}
	});
}
