let cur_path = window.location.pathname.split('/');
cur_path = cur_path.filter(function(element) {
    return element !== "";
});

// $("body").hide();

frappe.ready(function() {

	//if(frappe.user_id == "Guest" && (cur_path.length != 1 || cur_path[0] != "login")) {
	//	window.location.href = '/login';
	//} else {
	//	$("body").show();
	//}

	//Check routes

	if((cur_path.length == 1 && cur_path[0] == 'products') || cur_path.length == 0) {
		// render products page
	}

	if(cur_path.length > 1 && cur_path[0] == 'products') {
		render_item_page();
	}

	if(cur_path.length == 1 && cur_path[0] == 'cart') {
		render_cart_page();
	}

	if(cur_path.length == 1 && cur_path[0] == 'quotations') {
		render_quotations_page();
	}

	if(cur_path.length > 1 && cur_path[0] == 'quotations') {
		render_quotation_page();
	}

  if(cur_path.length == 1 && cur_path[0] == 'orders') {
		render_orders_page();
	}

  if(cur_path.length > 1 && cur_path[0] == 'orders') {
		render_order_page();
	}

	if(cur_path.length > 1 && (cur_path[0] == 'invoices'
	|| cur_path[0] == 'orders')) {
		remove_delivered_count();
	}

	if(cur_path.length > 1 && (cur_path[0] == 'quotations'
	|| cur_path[0] == 'orders'
	|| cur_path[0] == 'shipments'
	|| cur_path[0] == 'invoices')) {
		$(".page-header-actions-block").hide();
	}

});

// Page render functions

function render_item_page() {
	$(".item-stock").hide();
	$(".item-cart").addClass("dont-hide");
	get_user_price();
}

function render_cart_page() {
	$(".page-header-actions-block").append("<button class='btn btn-primary btn-complete-order btn-sm'>Submit Purchase Request</button>");
	$("button.btn-place-order").hide();
	$(".btn-complete-order").click(function(){

		place_order();

	});
}

function render_quotations_page() {
	$(".page-head h1").html("Purchase Requests");
}

function render_quotation_page() {
	$(".page-breadcrumbs a[href='/quotations'] span").html("Purchase Requests");
}

function render_orders_page() {
	$(".page-head h1").html("Approved Orders");
}

function render_order_page() {
	$(".page-breadcrumbs a[href='/orders'] span").html("Approved Orders");
}

function remove_delivered_count() {
	$(".row.order-items div:nth-child(2) p.text-muted.small").each(function () {
		$(this).hide();
	});
}

function place_order() {
	return frappe.call({
		type: "POST",
		method: "origintheme.api.place_order",
		callback: function(r) {
			if(r.exc) {
				var msg = "";
				if(r._server_messages) {
					msg = JSON.parse(r._server_messages || []).join("<br>");
				}

				$("#cart-error")
					.empty()
					.html(msg || frappe._("Something went wrong!"))
					.toggle(true);
			} else {
				window.location.href = "/quotations/" + encodeURIComponent(r.message);
			}
		}
	});
}

function get_user_price() {
	var item_code = $('[itemprop="productID"]').html();
	frappe.call({
		type: "POST",
		method: "origintheme.api.get_item_price",
		args: {
			item_code: item_code
		},
		callback: function(r) {
			if(r.message && r.message.price_list_rate) {
				var price = r.message;
				price.price_list_rate = price.price_list_rate.toFixed(2).toString();
				$("h4.item-price").addClass("hide");
				$("h4.item-price").parent().append("<h4>$ "+price.price_list_rate+" per "+price.stock_uom+"</h4>");
			}

		}
	});
}
